  number, get_odin_amount, create_time
  )
values (#{userId,jdbcType=INTEGER}, #{amount,jdbcType=DECIMAL}, #{buyPrice,jdbcType=DECIMAL}, 
  #{number,jdbcType=INTEGER}, #{getOdinAmount,jdbcType=DECIMAL}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from odin_buying_record
where 1=1 

   and  user_id = #{userId,jdbcType=INTEGER}


   and  amount = #{amount,jdbcType=DECIMAL}


   and  buy_price = #{buyPrice,jdbcType=DECIMAL}


   and  number = #{number,jdbcType=INTEGER}


   and  get_odin_amount = #{getOdinAmount,jdbcType=DECIMAL}


   and  create_time = #{createTime,jdbcType=TIMESTAMP}

 order by id desc

